        public static void SetLoopRadar(string _serverIP, int _port)
        {
            serverIP = _serverIP;
            port = _port;
            loopServer.LoopClientInput += SendSource;
            loopServer.RunLoopServer(port);

            isRunning = true;
            isRFCore = false;

            Task.Run(() => RunBITResponse());
        }

        private static async Task RunBITResponse()
        {
            var URL = $"http://{serverIP}:{port}/api/";


            httpListener = new HttpListener();
            httpListener.Prefixes.Add(URL);
            httpListener.Start();

            HttpListenerContext context;

            while (isRunning)
            {
                if (!httpListener.IsListening)
                {
                    Console.WriteLine("HTTP Listener 종료됨");
                    break;
                }
                context = httpListener.GetContext();

                var httpMethod = context.Request.HttpMethod;
                if (httpMethod != "GET")
                    break;

                var url = context.Request.RawUrl;
                switch (url)
                {
                    case "/api/v2/sources":
                        HttpListenerResponse response = context.Response;
                        SendText(ref context, ref response, sourceText);
                        break;

                    case "/api/v2/sources/1/configuration":
                        response = context.Response;
                        SendText(ref context, ref response, id1Text);
                        break;

                    default:
                        Console.WriteLine("잘못된 URL 입니다.");
                        continue;
                }

            }
        }

        private static void SendText(ref HttpListenerContext context, ref HttpListenerResponse response, string sourceText)
        {
            response.StatusCode = (int)HttpStatusCode.OK;
            response.ContentType = "text/plain"; // MIME 타입 설정
            using (var writer = new StreamWriter(response.OutputStream)) // 응답 본문 작성
            {
                writer.Write(sourceText);
            }
            response.Close(); // 응답 닫기
        }

        public static void StopLoopRadar()
        {
            loopServer.LoopClientInput -= SendSource;
            loopServer.DownServer();
            httpListener?.Close();
            httpListener = null;
            isRunning = false;
            isRFCore = true;
        }

        public static void SetRFCoreRadar(string _serverIP, int _port)
        {
            serverIP = _serverIP;
            port = _port;

            server = new TcpServer();
            server.OpenTCPServer(serverIP, port, out string message, true);
            server.MessageSendEvent += LogSend;
            isRunning = true;
            isRFCore = true;
        }

        public static void StopRFCoreRadar()
        {
            server.CloseTCPServer();
            isRunning = false;
            isRFCore = false;
            ShowLog("RFCore", "Closed");
        }

        private static void LogSend(string message)
        {
            ShowLog("RFCore", message);
        }

        private static void ShowLog(string radar, string message)
        {
            DataSendEvent?.Invoke($"{radar} - {message}");
        }

        public static bool SendData(byte[] packets)
        {
            var i = 0;
            var startIndex = 0;
            var stxCount = 0;
            var etxCount = 0;
            try
            {
                while (i < packets.Length && isRunning)
                {
                    if (!isRFCore)
                    {
                        if (packets[i] == 0x7B) stxCount++;
                        if (packets[i] == 0x7D) etxCount++;

                        if (stxCount == etxCount)
                        {
                            byte[] sendData = new byte[i - startIndex + 1];
                            Buffer.BlockCopy(packets, startIndex, sendData, 0, sendData.Length);

                            var receivedMessage = Encoding.UTF8.GetString(sendData, 0, sendData.Length);
                            JObject jsonObject;
                            try
                            {
                                jsonObject = JObject.Parse(receivedMessage);
                            }
                            catch (JsonReaderException)
                            {
                                break;
                            }

                            SendRadarData(sendData); 
                            LoopSendPacket?.Invoke();
                            Task.Delay(timeOut).Wait();
                            startIndex = i + 1;
                            stxCount = 0;
                            etxCount = 0;
                        }
                        i++;
                    }
                    else
                    {
                        if (i + 1000 >= packets.Length)
                        {
                            RFCoreSendPacket?.Invoke();
                            i = packets.Length - i;
                        }
                        else
                            i += 1000;
                        byte[] sendData = new byte[i - startIndex + 1];
                        Buffer.BlockCopy(packets, startIndex, sendData, 0, sendData.Length);

                        SendRadarData(sendData);
                        Task.Delay(timeOut).Wait();
                        i++;
                        startIndex = i;
                        stxCount = 0;
                        etxCount = 0;
                    }
                }
            }
            catch (IOException)
            {
                throw new IOException();
            }
            catch (Exception ex)
            {
                return false;
            }

            return false;
        }

        private static void SendSource()
        {
            var text = "{\r\n    \"sources\": [\r\n        {\r\n            \"id\": 1,\r\n            \"name\": \"Radar\",\r\n            \"state\": \"error\",\r\n            \"type\": \"radar\"\r\n        }]}";
            WebSocketServer.SendMessageToAll_Path(text, "/sources");
        }

        private static void SendRadarData(byte[] packets)
        {
            var text = Encoding.UTF8.GetString(packets);
            if (isRFCore)
            {
                if (server.IsConnectClient())
                {
                    server.SendData(packets);
                    ShowLog("RFCore", "Data 송신");
                }
            }
            else
            {
                WebSocketServer.SendMessageToAll_Path(text, "/sources/1/trajectories");
                ShowLog("Loop", "Data 송신");
            }
        }

        public static async void CloseEndPoint(string url)
        {
            await loopServer.CloseStreamTargetEndPoint(url);
        }

        public static async void OpenEndPoint(string url)
        {
            await loopServer.OpenStreamTargetEndPoint(url);
        }
